pragma solidity ^0.4.17;

contract Mercure {
  address public owner;
  uint public price;

  event Notification(address from, string to, string content);

  modifier paid() {
    assert(msg.value >= price);
    _;
  }

  modifier restricted() {
    assert(msg.sender == owner);
    _;
  }

  function Mercure(uint _price) 
    public 
  {
    owner = msg.sender;
    price = _price;
  }

  function changeOwner(address _newOwner)
      public
      restricted
  {
      owner = _newOwner;
  }

  function withdraw() public restricted {
      owner.transfer(this.balance);
  }

  function setPrice(uint _price) public restricted {
    price = _price;
  }

  function getPrice() public view returns( uint ) {
    return price;
  }

  function sendNotification(string _to, string content) public payable paid {
    Notification(msg.sender, _to, content);
  }
}
